import React from "react";

// ✅ Simple Card components
function Card({ children, className = "" }) {
  return (
    <div
      className={`rounded-xl border border-gray-300 shadow-md bg-white p-4 ${className}`}
    >
      {children}
    </div>
  );
}

function CardContent({ children, className = "" }) {
  return <div className={`p-2 ${className}`}>{children}</div>;
}

// ✅ Dashboard with horizontal scroll row
export default function Dashboard() {
  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-2xl font-bold mb-6">Dashboard</h1>

      {/* Horizontal scroll container */}
      <div className="flex space-x-6 overflow-x-auto pb-4">
        {/* Medicines Panel */}
        <Card className="min-w-[250px]">
          <CardContent>
            <h2 className="font-semibold mb-2">Medicines</h2>
            <ul className="list-disc list-inside">
              <li>Medicine 1 - 2 times a day</li>
              <li>Medicine 2 - Once before sleep</li>
            </ul>
          </CardContent>
        </Card>

        {/* Appointments Panel */}
        <Card className="min-w-[250px]">
          <CardContent>
            <h2 className="font-semibold mb-2">Appointments</h2>
            <ul className="list-disc list-inside">
              <li>Dr. Sharma - 12 Sep, 10:00 AM</li>
              <li>Dr. Mehta - 15 Sep, 2:00 PM</li>
            </ul>
          </CardContent>
        </Card>

        {/* Health Metrics Panel */}
        <Card className="min-w-[250px]">
          <CardContent>
            <h2 className="font-semibold mb-2">Health Metrics</h2>
            <p>Blood Pressure: 120/80 mmHg</p>
            <p>Heart Rate: 72 bpm</p>
          </CardContent>
        </Card>

        {/* Notifications Panel */}
        <Card className="min-w-[250px]">
          <CardContent>
            <h2 className="font-semibold mb-2">Notifications</h2>
            <p>You have 2 pending medication reminders today.</p>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
