// src/pages/Dashboard.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

const fadeUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Container = styled.div`
  padding: 2rem 3rem;
  max-width: 900px;
  margin: 2rem auto;
  font-family: 'Poppins', sans-serif;
`;

const Title = styled.h1`
  color: #334e68;
  font-size: 2.8rem;
  margin-bottom: 2rem;
  letter-spacing: 1.5px;
  font-weight: 700;
  animation: ${fadeUp} 0.7s ease forwards;
`;

const CardGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit,minmax(250px,1fr));
  gap: 1.8rem;
`;

const Card = styled.div`
  background: linear-gradient(135deg, #667eea, #764ba2);
  border-radius: 18px;
  color: white;
  box-shadow: 0 12px 24px rgba(102,126,234,0.5);
  padding: 2rem;
  animation: ${fadeUp} 0.9s ease forwards;
  transition: transform 0.3s ease;
  cursor: pointer;
  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(102,126,234,0.7);
  }
`;

const CardTitle = styled.h2`
  margin-bottom: 0.8rem;
  font-weight: 700;
  font-size: 1.4rem;
`;

const CardContent = styled.p`
  font-size: 1.1rem;
  opacity: 0.9;
  line-height: 1.5;
`;

export default function Dashboard() {
  return (
    <Container>
      <Title>Welcome to Mediflow Dashboard</Title>
      
      <CardGrid>
        <Card>
          <CardTitle>Pill Alerts</CardTitle>
          <CardContent>
            3 Active alerts: Vitamin D, Ibuprofen, Omega 3 - Stay on schedule!
          </CardContent>
        </Card>
        
        <Card>
          <CardTitle>Queue Status</CardTitle>
          <CardContent>
            Current queue length: 7<br />
            Next patient: Sarah Connor
          </CardContent>
        </Card>
        
        <Card>
          <CardTitle>Notifications</CardTitle>
          <CardContent>
            No new messages or alerts right now. Check back later!
          </CardContent>
        </Card>
      </CardGrid>
    </Container>
  );
}
