import React, { createContext, useState, useContext } from 'react';

const UserContext = createContext();

export function UserProvider({ children }) {
  const [user, setUser] = useState(null);
  const [medications, setMedications] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [emergencyContact, setEmergencyContact] = useState('');
  const [history, setHistory] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [queuePosition, setQueuePosition] = useState(null);

  return (
    <UserContext.Provider
      value={{
        user,
        setUser,
        medications,
        setMedications,
        appointments,
        setAppointments,
        emergencyContact,
        setEmergencyContact,
        history,
        setHistory,
        doctors,
        setDoctors,
        queuePosition,
        setQueuePosition,
      }}
    >
      {children}
    </UserContext.Provider>
  );
}

// Custom hooks for easier context access

export const useUser = () => useContext(UserContext).user;
export const useSetUser = () => useContext(UserContext).setUser;

export const useMedications = () => useContext(UserContext).medications;
export const useSetMedications = () => useContext(UserContext).setMedications;

export const useAppointments = () => useContext(UserContext).appointments;
export const useSetAppointments = () => useContext(UserContext).setAppointments;

export const useEmergencyContact = () => useContext(UserContext).emergencyContact;
export const useSetEmergencyContact = () => useContext(UserContext).setEmergencyContact;

export const useHistory = () => useContext(UserContext).history;
export const useSetHistory = () => useContext(UserContext).setHistory;

export const useDoctors = () => useContext(UserContext).doctors;
export const useSetDoctors = () => useContext(UserContext).setDoctors;

export const useQueuePosition = () => useContext(UserContext).queuePosition;
export const useSetQueuePosition = () => useContext(UserContext).setQueuePosition;
