import React, { useState } from 'react';

const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

export default function MedicineWeekPage() {
  const [weekMedicines, setWeekMedicines] = useState({
    Monday: [],
    Tuesday: [],
    Wednesday: [],
    Thursday: [],
    Friday: [],
    Saturday: [],
    Sunday: [],
  });

  // Simple add function for demo - extend as needed with proper input form
  const addMedicineForDay = (day, medicine) => {
    setWeekMedicines(prev => ({
      ...prev,
      [day]: [...prev[day], medicine],
    }));
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto' }}>
      <h2>Medicine Schedule for the Week</h2>
      {days.map(day => (
        <div key={day} style={{ marginBottom: 20 }}>
          <h3>{day}</h3>
          {weekMedicines[day].length === 0 ? (
            <p>No medicines scheduled.</p>
          ) : (
            <ul>
              {weekMedicines[day].map((med, idx) => (
                <li key={idx}>
                  {med.name} at {med.time} - {med.dose} mg
                </li>
              ))}
            </ul>
          )}
          {/* Implement add medicine input form here if wanted */}
        </div>
      ))}
    </div>
  );
}
