import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const MedicineDetails = styled.div`
  margin-top: 1rem;
  background: #f0f4f9;
  padding: 1rem;
  border-radius: 8px;
`;

const BrandInfo = styled.div`
  margin-bottom: 1rem;
  padding: 0.8rem;
  background: white;
  border-radius: 6px;
  box-shadow: 0 1px 5px rgba(0,0,0,0.1);
`;

const StoreList = styled.ul`
  margin-top: 0.5rem;
  list-style-type: none;
  padding-left: 0;
`;

const StoreItem = styled.li`
  margin-bottom: 0.5rem;
`;

export default function GenericMedicinePanel({ medicineName }) {
  const [medicineData, setMedicineData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!medicineName) {
      setMedicineData(null);
      setError('');
      return;
    }

    const fetchData = async () => {
      setLoading(true);
      setError('');
      try {
        const response = await axios.get(`/api/medicine_data?name=${encodeURIComponent(medicineName)}`);
        if (response.data && response.data.medicine) {
          setMedicineData(response.data);
        } else {
          setMedicineData(null);
          setError('No data found for this medicine.');
        }
      } catch (err) {
        setMedicineData(null);
        setError('Failed to fetch medicine data. Please try again later.');
      }
      setLoading(false);
    };

    // Debounce fetch if needed or just call immediately
    fetchData();
  }, [medicineName]);

  if (loading) return <p>Loading medicine data...</p>;
  if (error) return <p style={{color: 'red'}}>{error}</p>;
  if (!medicineData || !medicineData.medicine || medicineData.medicine.length === 0)
    return <p>No medicine information to display.</p>;

  return (
    <MedicineDetails>
      {medicineData.medicine.map((med, idx) => (
        <BrandInfo key={idx}>
          <h3>Brand: {med.brand_name}</h3>
          <p>Generic: {med.generic_name}</p>
          <p>Brand Price: ₹{med.brand_price}</p>
          <p>Generic Price: ₹{med.generic_price}</p>
          <h4>Available at Local Stores:</h4>
          <StoreList>
            {med.stores && med.stores.length > 0 ? (
              med.stores.map((store, i) => (
                <StoreItem key={i}>
                  <strong>{store.name}</strong> - {store.contact} {store.delivery ? "(Delivery Available)" : "(No Delivery)"}
                </StoreItem>
              ))
            ) : (
              <li>No stores available</li>
            )}
          </StoreList>
        </BrandInfo>
      ))}
    </MedicineDetails>
  );
}
