import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { UserContext } from '../../context/UserContext';
import { useNavigate } from 'react-router-dom';
import GenericMedicinePanel from '../../components/GenericMedicinePanel';
import HistoryTimeline from '../../components/HistoryTimeline';

// A small MedicineDay component you can also extract if needed
function MedicineDay({ medicines, addMedicine }) {
  const [name, setName] = useState('');
  const [time, setTime] = useState('');
  const [dose, setDose] = useState('');

  function handleSubmit(e) {
    e.preventDefault();
    if (!name || !time || !dose) {
      alert('Please fill all medicine fields');
      return;
    }
    addMedicine({ name, time, dose });
    setName('');
    setTime('');
    setDose('');
  }

  return (
    <div>
      <ul>
        {medicines.length === 0 ? (
          <li>No medicines scheduled for today.</li>
        ) : (
          medicines.map((med, idx) => (
            <li key={idx}>
              {med.name} at {med.time} - {med.dose} mg
            </li>
          ))
        )}
      </ul>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Medicine Name"
          value={name}
          onChange={e => setName(e.target.value)}
          style={{ marginRight: '8px' }}
        />
        <input
          type="time"
          value={time}
          onChange={e => setTime(e.target.value)}
          style={{ marginRight: '8px' }}
        />
        <input
          type="number"
          placeholder="Dose (mg)"
          value={dose}
          onChange={e => setDose(e.target.value)}
          min="0"
          style={{ marginRight: '8px', width: '80px' }}
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #2174b4ff, #04364eff);
  padding: 3rem 2rem;
  font-family: 'Poppins', sans-serif;
  color: white;
`;

const Title = styled.h1`
  color: #a8a1a1ff;
  margin-bottom: 2rem;
  text-shadow: 0 2px 6px #711010ff;
`;

const PanelGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1.5rem;
`;

const Panel = styled.div`
  background: rgba(69, 2, 146, 0.95);
  border-radius: 15px;
  padding: 1.5rem 2rem;
  box-shadow: 0 5px 20px rgb(0 0 0 / 0.3);
  cursor: pointer;
  transition: all 0.3s ease;
  &:hover {
    box-shadow: 0 7px 30px rgb(0 0 0 / 0.5);
  }
`;

const PanelTitle = styled.h2`
  margin-bottom: 1rem;
  font-weight: 700;
  color: #142f43;
`;

export default function Dashboard() {
  const {
    user,
    medications,
    appointments,
    emergencyContact,
    history,
    doctors,
    queuePosition,
  } = useContext(UserContext);

  const navigate = useNavigate();

  // For demo: show only today's medicines (e.g., filter by date or static)
  const [todayMedicines, setTodayMedicines] = useState(medications.slice(0, 5));

  function addMedicine(medicine) {
    setTodayMedicines(prev => [...prev, medicine]);
    // Optionally update context or backend here later
  }

  return (
    <Container>
      <Title>Welcome, {user ? user.name : 'User'}!</Title>
      <PanelGrid>
        {/* Medicines Panel with add + listing */}
        <Panel onClick={() => navigate('/dashboard/medicines')}>
          <PanelTitle>Medicines of the Week</PanelTitle>
          <MedicineDay medicines={todayMedicines} addMedicine={addMedicine} />
          <p style={{ textAlign: 'center', marginTop: 8, fontSize: 12 }}>
            Click to see full week schedule
          </p>
        </Panel>

        {/* Appointments Panel */}
        <Panel onClick={() => navigate('/dashboard/appointments')}>
          <PanelTitle>Your Appointments</PanelTitle>
          {appointments.length > 0 ? (
            <p>You have {appointments.length} upcoming appointment(s)</p>
          ) : (
            <p>No appointments scheduled yet</p>
          )}
        </Panel>

        {/* Emergency Assistant */}
        <Panel
          onClick={() => {
            if (emergencyContact) alert(`Sending emergency message to ${emergencyContact}`);
            else alert('Please set up your emergency contact.');
          }}
        >
          <PanelTitle>Emergency Assistant</PanelTitle>
          <p>Click to send emergency message.</p>
        </Panel>

        {/* History Timeline */}
        <Panel>
          <PanelTitle>History / Timeline</PanelTitle>
          <HistoryTimeline />
        </Panel>

        {/* Doctor Profiles Panel */}
        <Panel onClick={() => alert('Doctor profiles page coming soon')}>
          <PanelTitle>Doctor Profiles / Reviews</PanelTitle>
          {doctors.length > 0 ? (
            <ul>
              {doctors.map((doc, idx) => (
                <li key={idx}>
                  {doc.name} ({doc.specialty})
                </li>
              ))}
            </ul>
          ) : (
            <p>Loading doctors...</p>
          )}
        </Panel>

        {/* Symptom Checker Panel */}
        <Panel onClick={() => alert('Symptom Checker Chatbot coming soon')}>
          <PanelTitle>Symptom Checker Chatbot</PanelTitle>
          <p>Talk to get medication suggestions.</p>
        </Panel>

        {/* Clinic Queue Position */}
        <Panel>
          <PanelTitle>Clinic Queue Position</PanelTitle>
          <p>Your queue position: {queuePosition || 'N/A'}</p>
        </Panel>

        {/* Generic Medicine Price Comparison */}
        <Panel>
          <PanelTitle>Generic Medicine Price Comparison</PanelTitle>
          <input
            type="text"
            placeholder="Enter medicine name"
            style={{
              width: '100%',
              padding: '8px',
              marginBottom: '10px',
              borderRadius: 6,
              border: '1.5px solid #ccc',
            }}
            value={medicinesName}
            onChange={e => setMedicineName(e.target.value)}
          />
          <GenericMedicinePanel medicineName={medicinesName} />
        </Panel>
      </PanelGrid>
    </Container>
  );
}
