import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom'; // For navigation after signup

const PageContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #1e3c72, #2a5298);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  font-family: 'Poppins', sans-serif;
  color: white;
  padding: 2rem;
`;

const Title = styled.h1`
  font-size: 3rem;
  margin-bottom: 1.5rem;
`;

const FormContainer = styled.form`
  background: rgba(255 255 255 / 0.1);
  padding: 2rem;
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0,0,0,0.3);
  width: 100%;
  max-width: 400px;
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  padding: 12px;
  margin-bottom: 15px;
  border-radius: 6px;
  border: none;
  font-size: 1rem;
`;

const Button = styled.button`
  padding: 12px;
  background-color: #2a5298;
  color: white;
  font-size: 1.1rem;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
  transition: background-color 0.3s;
  &:hover {
    background-color: #1e3c72;
  }
`;

export default function SignUpPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  // Daily medicine state
  const [medicineName, setMedicineName] = useState('');
  const [medicineTime, setMedicineTime] = useState('');
  const [medicineDose, setMedicineDose] = useState('');
  
  const navigate = useNavigate();

  function handleSubmit(e) {
    e.preventDefault();

    // Collect all data for submission
    const signupData = {
      name,
      email,
      password,
      dailyMedicine: {
        name: medicineName,
        time: medicineTime,
        dose: medicineDose,
      }
    };

    // Replace alert with actual submission logic
    alert(`Signing up with:\nName: ${name}\nEmail: ${email}\nPassword: ${password}` +
          `\nDaily Medicine:\n- Name: ${medicineName}\n- Time: ${medicineTime}\n- Dose: ${medicineDose}`);

    // Example: redirect to dashboard or login page
    navigate('/dashboard');
  }

  return (
    <PageContainer>
      <Title>Create a New Account</Title>
      <FormContainer onSubmit={handleSubmit}>
        <Input
          type="text"
          placeholder="Name"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <Input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <Input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />

        {/* New inputs for daily medicine */}
        <Input
          type="text"
          placeholder="Daily Medicine Name"
          value={medicineName}
          onChange={e => setMedicineName(e.target.value)}
        />
        <Input
          type="text"
          placeholder="Daily Medicine Time (e.g., 9:00 AM)"
          value={medicineTime}
          onChange={e => setMedicineTime(e.target.value)}
        />
        <Input
          type="number"
          placeholder="Daily Medicine Dose (mg)"
          value={medicineDose}
          onChange={e => setMedicineDose(e.target.value)}
          min="0"
        />

        <Button type="submit">Sign Up</Button>
      </FormContainer>
    </PageContainer>
  );
}
