import React from "react";
import { useForm } from "react-hook-form";
import styled from "styled-components";

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 15px;
  border-radius: 8px;
  border: 1.5px solid #ccc;
  &:focus {
    border-color: #764ba2;
    outline: none;
  }
`;

const Button = styled.button`
  padding: 12px;
  background: #764ba2;
  color: white;
  font-weight: 700;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  &:hover {
    background: #6a11cb;
  }
`;

const Error = styled.span`
  color: red;
  font-size: 0.9rem;
  margin-bottom: 10px;
`;

export default function SignUpForm({ onSignUp }) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    if (localStorage.getItem(data.email)) {
      alert("Email already registered, try logging in.");
      return;
    }
    const userData = { name: data.name, email: data.email, password: data.password };
    localStorage.setItem(data.email, JSON.stringify(userData));
    alert(`${data.name}, registration successful! Please log in.`);
    onSignUp();
  };

  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <Input
        placeholder="Full Name"
        {...register("name", { required: true })}
      />
      {errors.name && <Error>Name is required</Error>}

      <Input
        type="email"
        placeholder="Email"
        {...register("email", { required: true })}
      />
      {errors.email && <Error>Email is required</Error>}

      <Input
        type="password"
        placeholder="Password"
        {...register("password", { required: true, minLength: 6 })}
      />
      {errors.password && (
        <Error>Password must be at least 6 characters</Error>
      )}

      <Button type="submit">Sign Up</Button>
    </Form>
  );
}
