import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";

const Container = styled.div`
  min-height: 100vh;
  background: url('/assets/bg-dashboard.jpg') no-repeat center center/cover;
  padding: 3rem 2rem;
  font-family: 'Poppins', sans-serif;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
  text-shadow: 0 2px 6px #000;
`;

const PanelGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1.5rem;
`;

const Panel = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 15px;
  padding: 1.5rem 2rem;
  box-shadow: 0 5px 20px rgb(0 0 0 / 0.3);
  cursor: pointer;
  transition: all 0.3s ease;
  &:hover {
    box-shadow: 0 7px 30px rgb(0 0 0 / 0.5);
  }
`;

const PanelTitle = styled.h2`
  margin-bottom: 1rem;
  font-weight: 700;
`;

export default function Dashboard() {
  const [medicines, setMedicines] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/dashboard/medicines")
      .then((res) => res.json())
      .then((data) => setMedicines(data))
      .catch((err) => console.error("Error fetching medicines:", err));
  }, []);

  return (
    <Container>
      <Title>Welcome to Dashboard!</Title>
      <PanelGrid>

        {/* Medicines Panel */}
        <Panel onClick={() => navigate("/dashboard/medicines")}>
          <PanelTitle>Medicines of the Week</PanelTitle>
          {medicines.length > 0 ? (
            <ul>
              {medicines.map((med) => (
                <li key={med.id}>
                  {med.brand_name} ({med.generic_name}) - â‚¹{med.brand_price}
                </li>
              ))}
            </ul>
          ) : (
            <p>No medicines found.</p>
          )}
        </Panel>

      </PanelGrid>
    </Container>
  );
}
