import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Pill,
  Calendar,
  PhoneCall,
  History,
  UserCircle,
  Bot,
  ListChecks,
} from "lucide-react";

export default function Dashboard() {
  const navigate = useNavigate();

  const panels = [
    {
      title: "Medicines of the Week",
      description: "View your daily medicines, doses & timings",
      route: "/medicines",
      icon: <Pill className="w-10 h-10 text-indigo-500" />,
    },
    {
      title: "Appointments of the Week",
      description: "Check upcoming doctor visits",
      route: "/appointments",
      icon: <Calendar className="w-10 h-10 text-pink-500" />,
    },
    {
      title: "Emergency Assistant",
      description: "Quickly alert your emergency contact",
      route: "/emergency",
      icon: <PhoneCall className="w-10 h-10 text-red-500" />,
    },
    {
      title: "History / Timeline",
      description: "Log your symptoms & track discomfort",
      route: "/history",
      icon: <History className="w-10 h-10 text-yellow-500" />,
    },
    {
      title: "Doctor Profiles / Reviews",
      description: "Find nearby doctors & read reviews",
      route: "/doctors",
      icon: <UserCircle className="w-10 h-10 text-green-500" />,
    },
    {
      title: "Symptom Checker ðŸ¤–",
      description: "Chatbot that suggests care or appointments",
      route: "/symptom-checker",
      icon: <Bot className="w-10 h-10 text-blue-500" />,
    },
    {
      title: "Smart Clinic Queue",
      description: "Check your position in the clinic queue",
      route: "/queue",
      icon: <ListChecks className="w-10 h-10 text-purple-500" />,
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-blue-100 to-purple-200 p-8">
      <motion.h1
        initial={{ y: -30, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="text-4xl font-bold text-indigo-700 mb-8 text-center"
      >
        ðŸŒŸ Welcome to Your Smart Health Dashboard
      </motion.h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {panels.map((panel, idx) => (
          <motion.div
            key={idx}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.97 }}
            transition={{ duration: 0.2 }}
          >
            <Card
              onClick={() => navigate(panel.route)}
              className="cursor-pointer bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all"
            >
              <CardContent className="flex flex-col items-center p-6 space-y-4 text-center">
                {panel.icon}
                <h2 className="text-xl font-semibold text-indigo-700">
                  {panel.title}
                </h2>
                <p className="text-gray-600 text-sm">{panel.description}</p>
              </CardContent>
            </Card>
          </motion.div>
        ))}
      </div>
    </div>
  );
}
