import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Pill,
  Calendar,
  PhoneCall,
  History,
  UserCircle,
  Bot,
  ListChecks,
} from "lucide-react";

export default function Dashboard() {
  const navigate = useNavigate();

  const widgets = [
    {
      title: "Medicines of the Week",
      description: "View your daily medicines, doses & timings",
      route: "/medicines",
      icon: <Pill className="w-12 h-12 text-indigo-500" />,
      bg: "bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100",
    },
    {
      title: "Appointments of the Week",
      description: "Check upcoming doctor visits",
      route: "/appointments",
      icon: <Calendar className="w-12 h-12 text-pink-500" />,
      bg: "bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100",
    },
    {
      title: "Emergency Assistant",
      description: "Quickly alert your emergency contact",
      route: "/emergency",
      icon: <PhoneCall className="w-12 h-12 text-red-500" />,
      bg: "bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100",
    },
    {
      title: "History / Timeline",
      description: "Log your symptoms & track discomfort",
      route: "/history",
      icon: <History className="w-12 h-12 text-yellow-500" />,
      bg: "bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100",
    },
    {
      title: "Doctor Profiles / Reviews",
      description: "Find nearby doctors & read reviews",
      route: "/doctors",
      icon: <UserCircle className="w-12 h-12 text-green-500" />,
      bg: "bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100",
    },
    {
      title: "Symptom Checker ü§ñ",
      description: "Chat with our symptom assistant",
      route: "/symptom-checker",
      icon: <Bot className="w-12 h-12 text-blue-500" />,
      bg: "bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100",
    },
    {
      title: "Smart Clinic Queue",
      description: "Check your position in the queue",
      route: "/queue",
      icon: <ListChecks className="w-12 h-12 text-purple-500" />,
      bg: "bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100",
    },
  ];

  return (
  <div className="min-h-screen bg-gradient-to-br from-blue-200 via-blue-100 to-blue-300 p-8">
    <motion.h1
      initial={{ y: -20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6 }}
      className="text-4xl font-bold text-blue-900 mb-10 text-center"
    >
      üå§Ô∏è Smart Health Dashboard
    </motion.h1>

    {/* Horizontally scrollable widget row */}
    <div className="flex space-x-6 overflow-x-auto pb-4 scrollbar-hide">
      {widgets.map((widget, idx) => (
        <motion.div
          key={idx}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.97 }}
          transition={{ duration: 0.2 }}
          onClick={() => navigate(widget.route)}
          className="flex-shrink-0 w-72"
        >
          <div
            className={`${widget.bg} rounded-3xl shadow-xl cursor-pointer p-6 flex flex-col items-center text-center hover:shadow-2xl transition-all`}
          >
            {widget.icon}
            <h2 className="text-xl font-semibold text-blue-900 mt-4">
              {widget.title}
            </h2>
            <p className="text-gray-700 mt-2">{widget.description}</p>
          </div>
        </motion.div>
      ))}
    </div>
  </div>
)
};
