// src/App.js
import React, { useState, useEffect } from 'react';
import { Alert } from './components/Alert';
import styles from './App.module.css';

function App() {
  const [pillAlerts, setPillAlerts] = useState([]);
  const [queueStatus, setQueueStatus] = useState({ queue_length: 0, next_patient: '' });

  useEffect(() => {
    // Simulate fetching from API
    setPillAlerts([
      { time: '08:00 AM', medication: 'Vitamin D' },
      { time: '12:00 PM', medication: 'Paracetamol' },
      { time: '06:00 PM', medication: 'Ibuprofen' },
    ]);
    setQueueStatus({ queue_length: 5, next_patient: 'John Doe' });
  }, []);

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h1>Mediflow Dashboard</h1>
      </header>

      <section className={styles.section}>
        <h2>Pill Alerts:</h2>
        <ul className={styles.alertList}>
          {pillAlerts.map((alert, idx) => (
            <Alert key={idx} time={alert.time} medication={alert.medication} />
          ))}
        </ul>
      </section>

      <section className={styles.section}>
        <h2>Queue Status:</h2>
        <p>Queue Length: <strong>{queueStatus.queue_length}</strong></p>
        <p>Next Patient: <strong>{queueStatus.next_patient}</strong></p>
      </section>
    </div>
  );
}

export default App;
